mapper.select:
SELECT  *
FROM    TRV_USER


        ID = #{id}


        AND PWD = #{encPwd}


        AND PH = #{ph}

mapper.update:
UPDATE  TRV_LOGIN_USER
SET

        IP_ADDR = #{ipAddr}
    ,   LOGIN_DT = SYSDATE()
    ,

        LOGIN_YN = #{loginYn}
    ,   MOD_DT = SYSDATE()
WHERE   ID = #{id}

mapper.insert:
INSERT INTO TRV_LOGIN_USER
(ID, IP_ADDR, LOGIN_DT, LOGIN_YN, MOD_DT)
VALUES(
        #{id}
    ,   #{ipAddr}
    ,   NOW()
    ,   'Y'
    ,   NOW()
)

INSERT INTO TRV_USER
(ID, USER_ID, PWD, PH)
VALUES(
        #{id}
   ,    #{userId}
   ,    #{encPwd}
   ,    CONCAT(#{ph1}, #{ph2}, #{ph3})
)
