mapper.insert:
INSERT INTO TRV_PLAN_DAY
(ID, USER_ID, NM, START_TM, END_TM, MOD_DT)
VALUES(
        CONCAT('PLD', LPAD(GET_SEQ('PLD'), 7, 0))
    ,   #{userId}
    ,   #{nm}
    ,   #{startTm}
    ,   #{endTm}
    ,   NOW()
)

mapper.update:
UPDATE TRV_PLAN_DAY
SET     NM = #{nm}
    ,   START_TM = #{startTm}
    ,   END_TM = #{endTm}
    ,   START_LOC_NM = #{startLocNm}
    ,   START_LOC_LAT = #{startLocLat}
    ,   START_LOC_LNG = #{startLocLng}
    ,   MOD_DT = NOW()
WHERE   ID = #{id}

mapper.select:
SELECT ID , USER_ID , NM , DATE_FORMAT(START_TM, '%H:%i') AS START_TM , DATE_FORMAT(END_TM, '%H:%i') AS END_TM , START_LOC_NM , START_LOC_LAT , START_LOC_LNG , MOD_DT FROM TRV_PLAN_DAY WHERE USER_ID = #{userId}
AND ID NOT IN
#{item.dayId}
ORDER BY
${sort}
NM

SELECT ID, DAY_ID , BOARD_ID , ORDR , DATE_FORMAT(EXPECTED_TM, '%H:%i') AS EXPECTED_TM , STAY_TM_MIN , TRAVEL_TM_MIN , MOD_DT FROM TRV_PLAN_DO WHERE
DAY_ID IN ( SELECT ID FROM TRV_PLAN_DAY WHERE USER_ID = #{userId}) AND BOARD_ID = #{boardId}
DAY_ID = #{dayId}

mapper.insert:
INSERT INTO TRV_PLAN_DO
(ID, DAY_ID, BOARD_ID, MOD_DT)
VALUES(
        CONCAT('PLO', LPAD(GET_SEQ('PLO'), 7, 0))
     ,  #{dayId}
     ,  #{boardId}
     ,  NOW()
)

mapper.update:
UPDATE TRV_PLAN_DO
SET     ORDR = #{ordr}
    ,   EXPECTED_TM = #{expectedTm}
    ,   STAY_TM_MIN = #{stayTmMin}
    ,   TRAVEL_TM_MIN = #{travelTmMin}
    ,   MOD_DT = NOW()
WHERE   ID = #{id}
